import React, { useState, useEffect } from "react";
import { useFetch } from "./useFetch";
import Mail from "./Mail";
import logo from './stable-logo.svg';
function App() {
  const { loading, data } = useFetch();
  const [page, setPage] = useState(0);
  const [mails, setMails] = useState([]);

  useEffect(() => {
    if (loading) return;
    setMails(data[page]);
  }, [loading, page]);

  const handlePage = (index) => {
    setPage(index);
  };

  const nextPage = () => {
    if (page === data.length - 1) {
      setPage(0);
    } else {
      setPage(page + 1);
    }
  };
  const prevPage = () => {
    if (page === 0) {
      setPage(data.length - 1);
    } else {
      setPage(page - 1);
    }
  };
  console.log("nusaiba")
  console.log(data)
  const total = 10;
  return (
    <main>
      <img src={logo} width="200" height="40" />
      <div className="underline"></div>
      <div className="section-title">
        <h1>{loading ? "loading..." : "All Mail"}</h1>
        <desc>Here are all of the pieces of mail you've reveived at your Stable address.</desc>

      </div>
      <section className="mails">
        <div className="container">
          {mails.map((mail) => {
            return <Mail key={mail.id} {...mail} />;
          })}
        </div>
        <div>{total}</div>
        <div className="btn-container">
          <button className="btn next-btn" onClick={() => prevPage()}>
            {!loading ? "Previous" : null}
          </button>
          {loading
            ? null
            : data.map((item, index) => {
                return (
                  <button
                    key={index}
                    className={`page-btn ${
                      index === page ? "active-btn" : null
                    }`}
                    onClick={() => {
                      handlePage(index);
                    }}
                  >
                    {index + 1}
                  </button>
                );
              })}
          <button className="btn next-btn" onClick={() => nextPage()}>
            {!loading ? "Next" : null}
          </button>
        </div>
      </section>
    </main>
  );
}

export default App;
